import React, { useState } from "react";
import { Alert, Button } from "react-bootstrap";
import "./App.css";

function AlertMessage() {
  const [showAlert, setShowAlert] = useState(true);

  const handleAlertClose = () => setShowAlert(false);

  return (
    <>
      {showAlert && (
        <div className="bdofalert">
          <Alert
            variant="info"
            onClose={handleAlertClose}
            dismissible
            id="alertpage"
          >
            Welcome eyyyyy
            <br />
            <br />
            <p>this site is powered by birthdayShu2023 team</p>
            <p>try to use UNKO-KUN(the cursor) to click on ANYTHING you can</p>
            <br />
            <br />
            credits:
            <br />
            <br />
            <a
              href="https://twitter.com/SHUBD2023"
              target="_blank"
              rel="noopener noreferrer"
            >
              project twitter{" "}
            </a>
            <a
              href="https://shu-birthday-2023.web.app/"
              target="_blank"
              rel="noopener noreferrer"
            >
              project website
            </a>
            <br />
            <br />
            <a
              href="https://github.com/BirShuDeyy2023/what-does-Shu-say"
              target="_blank"
              rel="noopener noreferrer"
            >
              maybe a github link?{" "}
            </a>
            <a
              href="https://impomu.com/"
              target="_blank"
              rel="noopener noreferrer"
            >
              insperation: I'm POMU site
              <br />
            </a>
            <Button
              variant="outline-info"
              size="lg"
              onClick={handleAlertClose}
              className="ml-2"
            >
              <br />{" "}
              <img src="./image/buttin_off.png" width={40} alt="alertoff" />
            </Button>
          </Alert>
        </div>
      )}
    </>
  );
}

export default AlertMessage;
